import React from "react";
import { Outlet } from "react-router-dom"; // React Router의 Outlet 사용
import Header from "./Header"; // 공통 헤더
import Sidebar from "./Sidebar"; // 선택적으로 사용하는 사이드바
import styled from "styled-components";

// 레이아웃 컨테이너 스타일
const LayoutContainer = styled.div`
  display: flex;
  flex-direction: column;
  min-height: 100vh;
`;

// 콘텐츠 및 사이드바를 포함하는 Wrapper
const ContentWrapper = styled.div`
  display: flex;
  flex: 1;
`;

// 메인 콘텐츠 영역
const MainContent = styled.main`
  flex: 1;
  padding: 20px;
`;

const Layout = () => {
  return (
    <LayoutContainer>
      <Header /> {/* 공통 헤더 */}
      <ContentWrapper>
        <Sidebar /> {/* 사이드바 */}
        <MainContent>
          <Outlet /> {/* Outlet을 사용하여 자식 컴포넌트를 렌더링 */}
        </MainContent>
      </ContentWrapper>
    </LayoutContainer>
  );
};

export default Layout;
