import styled from "styled-components";
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import { GlobalStyles } from "./styles/GlobalStyles.styles";
import { mixins } from "./styles/GlobalStyles.styles";
import Layout from "./components/layout/Layout"; 
import MainPage from "./pages/MainPage"; 
import LoginPage from "./pages/LoginPage"; 
import DetailPage from "./pages/DetailPage"; // 상세 페이지

// 스타일 컴포넌트
const Title = styled.h4`
  font-family: var(--pretendard);
  font-weight: normal;
  ${mixins.border({ color: "blue", width: 10 })};
`;

// 라우터 설정
const router = createBrowserRouter([
  {
    path: "/",
    element: <Layout />, // 공통 레이아웃
    children: [
      { path: "/", element: <MainPage /> }, // 메인 페이지
      { path: "/login", element: <LoginPage /> }, // 로그인 페이지
      { path: "/detail/:id", element: <DetailPage /> }, // 상세 페이지
    ],
  },
]);

function App() {
  return (
    <>
      <GlobalStyles /> {/* 전역 스타일 적용 */}
      <Title>테스트</Title>
      <RouterProvider router={router} /> {/* 라우터 제공 */}
    </>
  );
}

export default App;
